mapper.select:
select tname
from tutor
where userid= #{tutorId}

select classNum 
from class
where classname= #{className} and userid=#{tutorId}

select count(*)
from class
where userId= #{userId} and className= #{className}

select tintroduce
from tutor
where userid= #{userId}

select PROFILEPHOTO
from member
where userid= #{tutorId}

select count(*)
from tutor
where userid= #{tutorId}
